import React from 'react';
import { Route } from 'react-router-dom'
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import { fetchCollectionStartAsync } from '../../redux/shop/shop.actions'
import { selectCollectionIsFetching } from '../../redux/shop/shop.selector';

import CollectionOverview from '../../components/collection-overview/collection-overview.component';
import CollectionPage from '../collection/collection.component';
import WithSpinner from '../../components/with-spinner/with-spinner.component';

const CollectionsOverviewWithSpinner = WithSpinner(CollectionOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {

    componentDidMount() {
        fetchCollectionStartAsync();
    }

    render() {
        const { match } = this.props;
        const { isFetching } = this.props;
        return (
            <div className='shop-page'>
                <Route exact path={`${match.path}`} render={props => (
                    <CollectionsOverviewWithSpinner isLoading={isFetching} {...props} />
                )} />
                <Route path={`${match.path}/:collectionId`} render={(props) => <CollectionPageWithSpinner isLoading={isFetching} {...props} />} />
            </div>
        )
    }
}

const mapDispatchToProps = (dispatch) => ({
    fetchCollectionStartAsync: () =>
        dispatch(fetchCollectionStartAsync())
})

const mapStateToProps = createStructuredSelector({
    isCollectionFetching: selectCollectionIsFetching
})

export default connect(null, mapDispatchToProps)(ShopPage);
